import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Upload, FileText, Plus, Trash2, Eye, AlertCircle, CheckCircle, Loader2, Download, Search } from 'lucide-react';

interface MenuFile {
  id: number;
  name: string;
  date: string;
  status: 'processing' | 'processed' | 'optimized' | 'archived' | 'error';
  menuId?: number;
  items_count?: number;
  title?: string;
}

const MenuUpload: React.FC = () => {
  const { token } = useAuth();
  const [uploadedFiles, setUploadedFiles] = useState<MenuFile[]>([]);
  const [uploading, setUploading] = useState(false);
  const [uploadMessage, setUploadMessage] = useState<{ type: 'success' | 'error'; message: string } | null>(null);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchMenus();
  }, [token]);

  const fetchMenus = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:3001/api/menus', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        const formattedMenus = data.menus.map((menu: any) => ({
          id: menu.id,
          name: menu.title,
          date: new Date(menu.created_at).toLocaleDateString(),
          status: menu.status,
          menuId: menu.id,
          items_count: menu.items_count,
          title: menu.title
        }));
        setUploadedFiles(formattedMenus);
      } else {
        console.error('Failed to fetch menus');
      }
    } catch (error) {
      console.error('Error fetching menus:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (files && files.length > 0) {
      const file = files[0];
      setUploading(true);
      setUploadMessage(null);

      try {
        const formData = new FormData();
        formData.append('menu', file);
        formData.append('title', file.name.replace(/\.[^/.]+$/, '')); // Remove file extension for title

        const response = await fetch('http://localhost:3001/api/menu/upload', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
          body: formData,
        });

        if (response.ok) {
          const result = await response.json();
          
          // Add new menu to the list
          const newMenu: MenuFile = {
            id: Date.now(),
            name: file.name,
            date: new Date().toLocaleDateString(),
            status: 'processed',
            menuId: result.menuId,
            items_count: result.itemsCount
          };
          
          setUploadedFiles(prev => [newMenu, ...prev]);
          setUploadMessage({ type: 'success', message: `Menu uploaded successfully! ${result.itemsCount} items processed.` });
          
          // Refresh the menu list
          setTimeout(() => {
            fetchMenus();
          }, 1000);
        } else {
          const error = await response.json();
          setUploadMessage({ type: 'error', message: error.error || 'Upload failed' });
        }
      } catch (error) {
        console.error('Upload error:', error);
        setUploadMessage({ type: 'error', message: 'Failed to upload menu. Please try again.' });
      } finally {
        setUploading(false);
        // Clear the file input
        event.target.value = '';
      }
    }
  };

  const handleDeleteMenu = async (menuId: number) => {
    try {
      const response = await fetch(`http://localhost:3001/api/menu/${menuId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        setUploadedFiles(prev => prev.filter(menu => menu.menuId !== menuId));
        setUploadMessage({ type: 'success', message: 'Menu deleted successfully' });
      } else {
        setUploadMessage({ type: 'error', message: 'Failed to delete menu' });
      }
    } catch (error) {
      setUploadMessage({ type: 'error', message: 'Error deleting menu' });
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'processed':
        return 'text-blue-400';
      case 'optimized':
        return 'text-green-400';
      case 'archived':
        return 'text-gray-400';
      case 'error':
        return 'text-red-400';
      default:
        return 'text-gray-400';
    }
  };

  const getStatusBg = (status: string) => {
    switch (status) {
      case 'processed':
        return 'bg-blue-400/10';
      case 'optimized':
        return 'bg-green-400/10';
      case 'archived':
        return 'bg-gray-400/10';
      case 'error':
        return 'bg-red-400/10';
      default:
        return 'bg-gray-400/10';
    }
  };

  const filteredMenus = uploadedFiles.filter(menu =>
    menu.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-white mb-2">Menu Management</h1>
        <p className="text-gray-400">Upload and manage your restaurant menus for AI optimization.</p>
      </div>

      {/* Upload Section */}
      <div className="bg-gray-800 rounded-lg border border-gray-700 p-6 mb-8">
        <h2 className="text-xl font-semibold text-white mb-4">Upload New Menu</h2>
        
        <div className="border-2 border-dashed border-gray-600 rounded-lg p-8 text-center">
          <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-white mb-2">Upload your menu file</h3>
          <p className="text-gray-400 mb-4">Support for JSON, CSV, and Excel files (max 10MB)</p>
          
          <label className={`inline-flex items-center px-4 py-2 rounded-lg transition-colors cursor-pointer ${
            uploading 
              ? 'bg-gray-600 text-gray-300 cursor-not-allowed' 
              : 'bg-blue-600 text-white hover:bg-blue-700'
          }`}>
            {uploading ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Uploading...
              </>
            ) : (
              <>
                <Plus className="w-4 h-4 mr-2" />
                Choose File
              </>
            )}
            <input
              type="file"
              className="hidden"
              accept=".json,.csv,.xlsx,.xls"
              onChange={handleFileUpload}
              disabled={uploading}
            />
          </label>
        </div>

        {/* Upload Status Message */}
        {uploadMessage && (
          <div className={`mt-4 p-4 rounded-lg flex items-center space-x-2 ${
            uploadMessage.type === 'success' 
              ? 'bg-green-400/10 border border-green-400/20' 
              : 'bg-red-400/10 border border-red-400/20'
          }`}>
            {uploadMessage.type === 'success' ? (
              <CheckCircle className="w-5 h-5 text-green-400" />
            ) : (
              <AlertCircle className="w-5 h-5 text-red-400" />
            )}
            <span className={`text-sm ${
              uploadMessage.type === 'success' ? 'text-green-400' : 'text-red-400'
            }`}>
              {uploadMessage.message}
            </span>
          </div>
        )}
      </div>

      {/* Menu List */}
      <div className="bg-gray-800 rounded-lg border border-gray-700 p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-white">Your Menus</h2>
          <div className="flex items-center space-x-4">
            {/* Search */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search menus..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
            
            <button 
              onClick={() => document.querySelector('input[type="file"]')?.click()}
              className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              <Plus className="w-4 h-4" />
              <span>New Menu</span>
            </button>
          </div>
        </div>

        {loading ? (
          <div className="flex items-center justify-center py-8">
            <Loader2 className="w-6 h-6 animate-spin text-blue-400 mr-2" />
            <span className="text-gray-400">Loading menus...</span>
          </div>
        ) : (
          <div className="space-y-4">
            {filteredMenus.length > 0 ? (
              filteredMenus.map((file) => (
                <div key={file.id} className="flex items-center justify-between p-4 bg-gray-700 rounded-lg hover:bg-gray-650 transition-colors">
                  <div className="flex items-center space-x-4">
                    <FileText className="w-8 h-8 text-blue-400" />
                    <div>
                      <h3 className="text-white font-medium">{file.name}</h3>
                      <p className="text-gray-400 text-sm">
                        Uploaded on {file.date} • {file.items_count || 0} items
                      </p>
                    </div>
                  </div>
                  
                  <div className="flex items-center space-x-3">
                    <span className={`px-3 py-1 rounded-full text-xs font-medium ${getStatusColor(file.status)} ${getStatusBg(file.status)}`}>
                      {file.status}
                    </span>
                    
                    <button className="p-2 text-gray-400 hover:text-white hover:bg-gray-600 rounded-lg transition-colors">
                      <Eye className="w-4 h-4" />
                    </button>
                    
                    <button 
                      onClick={() => handleDeleteMenu(file.menuId!)}
                      className="p-2 text-gray-400 hover:text-red-400 hover:bg-gray-600 rounded-lg transition-colors"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8">
                <FileText className="w-12 h-12 text-gray-500 mx-auto mb-4" />
                <p className="text-gray-400">No menus found</p>
                <p className="text-sm text-gray-500 mt-1">
                  {searchTerm ? 'Try adjusting your search terms' : 'Upload your first menu to get started'}
                </p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default MenuUpload;
