import React, { useState, useEffect, useCallback } from 'react';
import { 
  AlertTriangle, 
  Calendar,
  Star,
  DollarSign,
  Target,
  XCircle,
  ArrowUpDown,
  Filter,
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  Minus
} from 'lucide-react';

interface MenuItem {
  id: number;
  name: string;
  price: number;
  cost: number;
  sales_count: number;
  tags: string;
}

interface MenuIQScore {
  score: number;
  category: 'excellent' | 'good' | 'average' | 'poor' | 'critical';
  breakdown: {
    profitMargin: number;
    salesVolume: number;
    wasteRisk: number;
    seasonalityRisk: number;
  };
  insights: string[];
  recommendations: string[];
  suggestedAction: string;
  actionType: 'promote' | 'remove' | 'reprice' | 'seasonal' | 'top-performer' | 'monitor';
  salesTrend: 'increasing' | 'stable' | 'declining';
}

interface MenuInsights {
  menuId: number;
  overallScore: number;
  itemScores: Array<{
    item: MenuItem;
    score: MenuIQScore;
  }>;
  categories: {
    profitability: MenuItem[];
    popularity: MenuItem[];
    waste: MenuItem[];
    seasonality: MenuItem[];
  };
  recommendations: string[];
  clusters: Array<{
    tag: string;
    items: MenuItem[];
    count: number;
  }>;
  actionCounts: {
    promote: number;
    remove: number;
    reprice: number;
    seasonal: number;
    'top-performer': number;
    monitor: number;
  };
}

type SortField = 'score' | 'margin' | 'sales' | 'name';
type SortDirection = 'asc' | 'desc';
type FilterType = 'all' | 'remove' | 'promote' | 'top-performer' | 'reprice' | 'seasonal';

const Insights: React.FC = () => {
  const [insights, setInsights] = useState<MenuInsights | null>(null);
  const [loading, setLoading] = useState(true);
  const [selectedMenu] = useState<number>(1);
  const [sortField, setSortField] = useState<SortField>('score');
  const [sortDirection, setSortDirection] = useState<SortDirection>('desc');
  const [filterType, setFilterType] = useState<FilterType>('all');

  useEffect(() => {
    fetchInsights();
  }, [selectedMenu, fetchInsights]);

  const fetchInsights = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:3001/api/menu/${selectedMenu}/insights`);
      if (response.ok) {
        const data = await response.json();
        setInsights(data);
      }
    } catch (error) {
      console.error('Failed to fetch insights:', error);
    } finally {
      setLoading(false);
    }
  }, [selectedMenu]);

  const getScoreColor = (score: number) => {
    if (score >= 85) return 'text-green-400';
    if (score >= 70) return 'text-blue-400';
    if (score >= 50) return 'text-yellow-400';
    if (score >= 30) return 'text-orange-400';
    return 'text-red-400';
  };

  const getActionColor = (actionType: string) => {
    switch (actionType) {
      case 'promote': return 'text-green-400 bg-green-400/10';
      case 'remove': return 'text-red-400 bg-red-400/10';
      case 'reprice': return 'text-orange-400 bg-orange-400/10';
      case 'seasonal': return 'text-blue-400 bg-blue-400/10';
      case 'top-performer': return 'text-purple-400 bg-purple-400/10';
      default: return 'text-gray-400 bg-gray-400/10';
    }
  };

  const getActionIcon = (actionType: string) => {
    switch (actionType) {
      case 'promote': return <TrendingUpIcon className="w-4 h-4" />;
      case 'remove': return <XCircle className="w-4 h-4" />;
      case 'reprice': return <DollarSign className="w-4 h-4" />;
      case 'seasonal': return <Calendar className="w-4 h-4" />;
      case 'top-performer': return <Star className="w-4 h-4" />;
      default: return <Target className="w-4 h-4" />;
    }
  };

  const getTrendIcon = (trend: string) => {
    switch (trend) {
      case 'increasing': return <TrendingUpIcon className="w-4 h-4 text-green-400" />;
      case 'declining': return <TrendingDownIcon className="w-4 h-4 text-red-400" />;
      default: return <Minus className="w-4 h-4 text-gray-400" />;
    }
  };

  const handleSort = (field: SortField) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('desc');
    }
  };

  const sortItems = (items: MenuInsights['itemScores']) => {
    return [...items].sort((a, b) => {
      let aValue: any, bValue: any;

      switch (sortField) {
        case 'score':
          aValue = a.score.score;
          bValue = b.score.score;
          break;
        case 'margin':
          aValue = ((a.item.price - a.item.cost) / a.item.price) * 100;
          bValue = ((b.item.price - b.item.cost) / b.item.price) * 100;
          break;
        case 'sales':
          aValue = a.item.sales_count;
          bValue = b.item.sales_count;
          break;
        case 'name':
          aValue = a.item.name.toLowerCase();
          bValue = b.item.name.toLowerCase();
          break;
        default:
          return 0;
      }

      if (sortDirection === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });
  };

  const filterItems = (items: MenuInsights['itemScores']) => {
    if (filterType === 'all') return items;
    return items.filter(item => item.score.actionType === filterType);
  };

  const getFilteredAndSortedItems = () => {
    if (!insights) return [];
    const filtered = filterItems(insights.itemScores);
    return sortItems(filtered);
  };

  if (loading) {
    return (
      <div className="p-6">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-accent-cyan"></div>
        </div>
      </div>
    );
  }

  if (!insights) {
    return (
      <div className="p-6">
        <div className="text-center text-gray-400">
          <AlertTriangle className="w-16 h-16 mx-auto mb-4" />
          <h3 className="text-xl font-semibold mb-2">No Insights Available</h3>
          <p>Upload a menu to see intelligent insights and recommendations.</p>
        </div>
      </div>
    );
  }

  const filteredItems = getFilteredAndSortedItems();

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-white mb-2">MenuIQ Insights</h1>
        <p className="text-gray-400">AI-powered menu optimization recommendations</p>
      </div>

      {/* Overall Score Card */}
      <div className="bg-gray-800 rounded-lg border border-gray-700 p-6 mb-8">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-xl font-semibold text-white">Overall Menu Health</h2>
          <div className="flex items-center space-x-4">
            <div className="text-sm text-gray-400">
              {insights.actionCounts.promote} to promote â€¢ {insights.actionCounts.remove} to remove
            </div>
            <div className={`text-2xl font-bold ${getScoreColor(insights.overallScore)}`}>
              {insights.overallScore}
            </div>
          </div>
        </div>
        
        <div className="flex items-center space-x-6">
          <div className="flex-1">
            <div className="w-full bg-gray-700 rounded-full h-3">
              <div 
                className={`h-3 rounded-full transition-all duration-300 ${
                  insights.overallScore >= 85 ? 'bg-green-400' :
                  insights.overallScore >= 70 ? 'bg-blue-400' :
                  insights.overallScore >= 50 ? 'bg-yellow-400' :
                  insights.overallScore >= 30 ? 'bg-orange-400' : 'bg-red-400'
                }`}
                style={{ width: `${insights.overallScore}%` }}
              ></div>
            </div>
            <p className="text-gray-400 text-sm mt-2">
              {insights.overallScore >= 85 ? 'Excellent' :
               insights.overallScore >= 70 ? 'Good' :
               insights.overallScore >= 50 ? 'Average' :
               insights.overallScore >= 30 ? 'Poor' : 'Critical'} performance
            </p>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="bg-gray-800 rounded-lg border border-gray-700 p-4 mb-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Filter className="w-5 h-5 text-gray-400" />
            <span className="text-white font-medium">Filter by Action:</span>
            <select
              value={filterType}
              onChange={(e) => setFilterType(e.target.value as FilterType)}
              className="bg-gray-700 text-white px-3 py-1 rounded border border-gray-600"
            >
              <option value="all">All Items ({insights.itemScores.length})</option>
              <option value="promote">Promote ({insights.actionCounts.promote})</option>
              <option value="remove">Remove ({insights.actionCounts.remove})</option>
              <option value="reprice">Reprice ({insights.actionCounts.reprice})</option>
              <option value="seasonal">Seasonal ({insights.actionCounts.seasonal})</option>
              <option value="top-performer">Top Performers ({insights.actionCounts['top-performer']})</option>
            </select>
          </div>
          <div className="text-sm text-gray-400">
            Showing {filteredItems.length} of {insights.itemScores.length} items
          </div>
        </div>
      </div>

      {/* Insights Table */}
      <div className="bg-gray-800 rounded-lg border border-gray-700 overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-700">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider">
                  Item Name
                </th>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider cursor-pointer hover:bg-gray-600"
                  onClick={() => handleSort('margin')}
                >
                  <div className="flex items-center space-x-1">
                    <span>Margin</span>
                    <ArrowUpDown className="w-4 h-4" />
                  </div>
                </th>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider cursor-pointer hover:bg-gray-600"
                  onClick={() => handleSort('sales')}
                >
                  <div className="flex items-center space-x-1">
                    <span>Sales Count</span>
                    <ArrowUpDown className="w-4 h-4" />
                  </div>
                </th>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider cursor-pointer hover:bg-gray-600"
                  onClick={() => handleSort('score')}
                >
                  <div className="flex items-center space-x-1">
                    <span>MenuIQ Score</span>
                    <ArrowUpDown className="w-4 h-4" />
                  </div>
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider">
                  Suggested Action
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider">
                  Trend
                </th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-700">
              {filteredItems.map((itemScore) => {
                const margin = ((itemScore.item.price - itemScore.item.cost) / itemScore.item.price) * 100;
                return (
                  <tr key={itemScore.item.id} className="hover:bg-gray-700">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-white">{itemScore.item.name}</div>
                        <div className="text-sm text-gray-400">${itemScore.item.price}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className={`text-sm font-medium ${
                        margin >= 50 ? 'text-green-400' :
                        margin >= 30 ? 'text-yellow-400' :
                        'text-red-400'
                      }`}>
                        {margin.toFixed(1)}%
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-white">{itemScore.item.sales_count}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className={`text-sm font-bold ${getScoreColor(itemScore.score.score)}`}>
                        {itemScore.score.score}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getActionColor(itemScore.score.actionType)}`}>
                        {getActionIcon(itemScore.score.actionType)}
                        <span className="ml-1">{itemScore.score.suggestedAction}</span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {getTrendIcon(itemScore.score.salesTrend)}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {/* Action Summary */}
      <div className="mt-6 grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-4">
          <div className="flex items-center space-x-2">
            <TrendingUpIcon className="w-5 h-5 text-green-400" />
            <span className="text-white font-medium">Promote</span>
          </div>
          <div className="text-2xl font-bold text-green-400">{insights.actionCounts.promote}</div>
        </div>
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-4">
          <div className="flex items-center space-x-2">
            <XCircle className="w-5 h-5 text-red-400" />
            <span className="text-white font-medium">Remove</span>
          </div>
          <div className="text-2xl font-bold text-red-400">{insights.actionCounts.remove}</div>
        </div>
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-4">
          <div className="flex items-center space-x-2">
            <DollarSign className="w-5 h-5 text-orange-400" />
            <span className="text-white font-medium">Reprice</span>
          </div>
          <div className="text-2xl font-bold text-orange-400">{insights.actionCounts.reprice}</div>
        </div>
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-4">
          <div className="flex items-center space-x-2">
            <Calendar className="w-5 h-5 text-blue-400" />
            <span className="text-white font-medium">Seasonal</span>
          </div>
          <div className="text-2xl font-bold text-blue-400">{insights.actionCounts.seasonal}</div>
        </div>
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-4">
          <div className="flex items-center space-x-2">
            <Star className="w-5 h-5 text-purple-400" />
            <span className="text-white font-medium">Top Performers</span>
          </div>
          <div className="text-2xl font-bold text-purple-400">{insights.actionCounts['top-performer']}</div>
        </div>
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-4">
          <div className="flex items-center space-x-2">
            <Target className="w-5 h-5 text-gray-400" />
            <span className="text-white font-medium">Monitor</span>
          </div>
          <div className="text-2xl font-bold text-gray-400">{insights.actionCounts.monitor}</div>
        </div>
      </div>

      {/* Clusters */}
      {insights.clusters.length > 0 && (
        <div className="mt-6">
          <h3 className="text-lg font-semibold text-white mb-4">Item Clusters</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {insights.clusters.map((cluster, index) => (
              <div key={index} className="bg-gray-800 rounded-lg border border-gray-700 p-4">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-white font-medium capitalize">{cluster.tag}</span>
                  <span className="text-gray-400 text-sm">{cluster.count} items</span>
                </div>
                <div className="space-y-1">
                  {cluster.items.slice(0, 3).map((item) => (
                    <div key={item.id} className="text-sm text-gray-300">
                      {item.name}
                    </div>
                  ))}
                  {cluster.items.length > 3 && (
                    <div className="text-sm text-gray-400">
                      +{cluster.items.length - 3} more
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Insights;
